import classnames from "classnames"
import { useState } from "react"
import { styleVibration, useSocketOn } from "./Utiles"
import { Socket } from "socket.io-client"

interface ModalLoginPorps {
    closeModal: () => void
    socket : Socket
}


const ModalCreateAccount: React.FC<ModalLoginPorps> = ({closeModal,socket}) => {
    const [userid, setUserid] = useState<String>()
    const [password,setPassword] = useState<String>()
    const [againPassword,setAgainPassword] = useState<String>()
    const [vibration, setVibration] = useState<boolean>(false)

    const send = () => {
        if (password === againPassword) {
            styleVibration(setVibration)
        }
        else {
            socket.emit('Create_account', {userid : userid, password: password});
        }
      };
    
    useSocketOn("Create_account", (data) => {
        if(data === "Success") {
            closeModal()
        }
        else if (data === "useridDuplicate") {
            alert("이미 존재하는 아이디 입니다.")
        }
        else {
            alert("알 수 없는 오류 발생")
        }
    })
    
    return <div className={classnames('createAccount', 'modal')}>
        <input className="userid" onChange={(e) => {setUserid(e.target.value)}} />
        <input className="newPassword" onChange={(e) => {setPassword(e.target.value)}} />
        <input className="againPassword" onChange={(e) => {setAgainPassword(e.target.value)}} />
        {password === againPassword? <p className={classnames('weakInput',{'vibration':vibration})}>비밀번호가 일치 하지 않습니다.</p> : null}
        <button onClick={send}>완료</button> <button onClick={closeModal}>취소</button>
    </div>
}


export default ModalCreateAccount
