import React,{ useEffect, useState } from "react";
import { socket } from "../App";
import {card} from './inter'


export const styleVibration = (setVibration:(vibration:boolean)=>void,t:number = 400) => {
    setVibration(true);
    setTimeout(function() {
        setVibration(false);
    }, t);
  }


export const useSocketOn = (ev:string, callBack:(data:any)=>void, deps:React.DependencyList = []) => {
    useEffect(() => {

        socket.on(ev, data => {
            callBack(data);
        })
        
        return () => {
            socket.off(ev);
        };
    }, deps);
}


const importAllImages = (r: __WebpackModuleApi.RequireContext): { [key: string]: string } => {
    let images: { [key: string]: string } = {};
    r.keys().forEach((item:any) => {
      images[item.replace('./', '')] = r(item).default;
    });
    return images;
  };
  
export const images = importAllImages(require.context('../', false, /\.(png|jpe?g|gif)$/));




export const getCardInfo = (id:number) => {
    const [card, serCard] = useState<card>()
    socket.emit('card',id)
    useSocketOn('card', (data) => {serCard(data)})
    return card
}
