import { useCallback, useEffect, useId, useState } from "react";
import { atom, useRecoilState } from "recoil";


export const modalState = atom<{ id: string; element: React.FC }[]>({
  key: 'modalState',
  default: [],
});


const useModal = (component: React.FC) => {
  const [modalElements, setModal] = useRecoilState(modalState);

  const isOpen = modalElements.length > 0;

  const openModal = () => {
    const id = `${component.displayName}-${new Date().getTime()}`;
    setModal([...modalElements, { id, element: component }]);
    document.body.style.overflow = 'hidden';
  };

  const closeModal = () => {
    setModal(modalElements.filter((c) => c.element !== component));
    if (modalElements.length <= 1) document.body.style.overflow = 'unset';
  };

  useEffect(() => {
    if (modalElements.length === 0) {
      document.body.style.overflow = 'unset';
    }
  }, [modalElements.length]);

  return { isOpen, openModal, closeModal };
};

export default useModal;
