import React,{ useEffect } from "react";
import { socket } from "../../App";

export const styleVibration = (setVibration:(vibration:boolean)=>void,t:number = 400) => {
    setVibration(true);
    setTimeout(function() {
        setVibration(false);
    }, t);
  }


export const useSocketOn = (ev:string, callBack:(data:any)=>void, deps:React.DependencyList = []) => {
    useEffect(() => {

        socket.on(ev, data => {
            callBack(data);
        })
        
        return () => {
            socket.off(ev);
        };
    }, deps);
}


const importAllImages = (r: __WebpackModuleApi.RequireContext): { [key: string]: string } => {
    let images: { [key: string]: string } = {};
    r.keys().forEach((item:any) => {
      images[item.replace('./', '')] = r(item).default;
    });
    return images;
  };
  
  export const images = importAllImages(require.context('../assets/image/base', false, /\.(png|jpe?g|gif)$/));
