import React, { useEffect, useState } from "react";
import { Socket } from "socket.io-client";
import useModal from "./useModal";
import {styleVibration, useSocketOn} from './Utiles'
import classnames from 'classnames';



interface ModalLoginPorps {
    isOpen : boolean
    closeModal: () => void
    socket : Socket
}



const ModalLogin: React.FC<ModalLoginPorps> = ({isOpen,closeModal,socket}) => {
    console.log("login")
    const [userid, setUserid] = useState<String>()
    const [password,setPassword] = useState<String>()
    const send = () => {
        socket.emit('login', {userid : userid, password: password});
      };


    const {isOpen:isOpenCreateAccount,openModal:openModalCreateAccount,closeModal:closeModalCreateAccount} = useModal(() => 
        {return <ModalCreateAccount isOpen={isOpenCreateAccount} socket={socket} closeModal={closeModalCreateAccount} />})

    useSocketOn("login", (data) => {
        if(data === "Success") {
            closeModal()
        }
        else if (data === "useridError") {
            alert("존재하지 않는 아이디 입니다.")
        }
        else if (data === "passwordError") {
            alert("틀린 비밀번호")
        }
        else {
            alert("알 수 없는 오류 발생")
        }
    })



    return isOpen ? <div className="login">
        <input name="userid" onChange={(e) => {setUserid(e.target.value)}} />
        <input name="password" onChange={(e) => {setPassword(e.target.value)}} />
        <p onClick={openModalCreateAccount}>Create Account</p>
        <button onClick={send}>완료</button> <button onClick={closeModal}>취소</button>
    </div> : null;
}

const ModalCreateAccount: React.FC<ModalLoginPorps> = ({isOpen,closeModal,socket}) => {
    const [userid, setUserid] = useState<String>()
    const [password,setPassword] = useState<String>()
    const [againPassword,setAgainPassword] = useState<String>()
    const [vibration, setVibration] = useState<boolean>(false)

    const send = () => {
        if (password === againPassword) {
            styleVibration(setVibration)
        }
        else {
            socket.emit('Create_account', {userid : userid, password: password});
        }
      };
    
    useSocketOn("Create_account", (data) => {
        if(data === "Success") {
            closeModal()
        }
        else if (data === "useridDuplicate") {
            alert("이미 존재하는 아이디 입니다.")
        }
        else {
            alert("알 수 없는 오류 발생")
        }
    })
      
    return isOpen ? <div className="login">
        <input name="userid" onChange={(e) => {setUserid(e.target.value)}} />
        <input name="newPassword" onChange={(e) => {setPassword(e.target.value)}} />
        <input name="againPassword" onChange={(e) => {setAgainPassword(e.target.value)}} />
        {password === againPassword? <p className={classnames('weakInput',{'vibration':vibration})}>비밀번호가 일치 하지 않습니다.</p> : null}
        <button onClick={send}>완료</button> <button onClick={closeModal}>취소</button>
    </div> : null;
}





export default ModalLogin
