import React, { useState } from "react";
import { Socket } from "socket.io-client";
import useModal from "./useModal";
import {useSocketOn} from './Utiles'
import ModalCreateAccount from './ModalCreateAccount'
import { OutModal } from "./styles";
import styled from "styled-components";

interface ModalLoginPorps {
    closeModal: () => void
    socket : Socket
}



const ModalLogin: React.FC<ModalLoginPorps> = ({closeModal,socket}) => {
    const [userid, setUserid] = useState<String>()
    const [password,setPassword] = useState<String>()
    const send = () => {
        socket.emit('login', {userid : userid, password: password});
      };


    const {openModal:openModalCreateAccount,closeModal:closeModalCreateAccount} = useModal(() => 
        {return <ModalCreateAccount socket={socket} closeModal={closeModalCreateAccount} />})

    useSocketOn("login", (data) => {
        if(data === "Success") {
            closeModal()
        }
        else if (data === "useridError") {
            alert("존재하지 않는 아이디 입니다.")
        }
        else if (data === "passwordError") {
            alert("틀린 비밀번호")
        }
        else {
            alert("알 수 없는 오류 발생")
        }
    })
    return <>
    <OutModal/>
    <Login>
        <div>login</div>
        <UserInput className="userid" type="text" onChange={(e) => {setUserid(e.target.value)}} />
        <UserInput className="password" type="password" onChange={(e) => {setPassword(e.target.value)}} />
        <p onClick={() => {closeModal(); openModalCreateAccount();}}>Create Account</p>
        <button onClick={send}>완료</button> <button onClick={closeModal}>취소</button>
    </Login>
    </>

    
}

const Login = styled.div`
    z-index: 15;
    border: 1px solid black;
    background-color: white;
    width: 600px;
    height: 400px;
    position: fixed;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    text-align: center;
`;


const UserInput = styled.input`
    display: block;
    margin: 50px;
    width: 500px;
    height: 10px;
    
`;


export default ModalLogin
